{% tab ocr-cods-desc 파일 업로드 %}
``` php
<?php
  // 업로드한 파일을 바이너리로 읽어서 BASE64로 인코딩
  $ocr_upload_file = $_FILES["clova_ocr_upload_file"];
  $ocr_upload_file_tmp_name = $ocr_upload_file["tmp_name"];

