  // 전송할 값들을 배열 형태로 저장
  $postvars = [
    "version"=> "V2",
    "requestId"=> $ocr_request_id,
    "timestamp"=> $unixtimestamp,
    "lang"=> "ko",
    "images"=> [
      [
        "format"=> "jpg",
        "name"=> "ocr-test",
